/*
 * Initialize an instance of object `PropsFrame'.
 */
radio_PropsFrame_objects *
radio_PropsFrame_objects_initialize(radio_PropsFrame_objects *ip, Xv_opaque owner)
{
	if (!ip && !(ip = (radio_PropsFrame_objects *) calloc(1, sizeof (radio_PropsFrame_objects))))
		return (radio_PropsFrame_objects *) NULL;
	if (!ip->PropsFrame)
		ip->PropsFrame = radio_PropsFrame_PropsFrame_create(ip, owner);
	if (!ip->PropsControls)
		ip->PropsControls = radio_PropsFrame_PropsControls_create(ip, ip->PropsFrame);
	if (!ip->AutoScanItem)
		ip->AutoScanItem = radio_PropsFrame_AutoScanItem_create(ip, ip->PropsControls);
	if (!ip->ScanDelayItem)
		ip->ScanDelayItem = radio_PropsFrame_ScanDelayItem_create(ip, ip->PropsControls);
	if (!ip->SecondsMessage)
		ip->SecondsMessage = radio_PropsFrame_SecondsMessage_create(ip, ip->PropsControls);
	if (!ip->ScanDelayGroup)
		ip->ScanDelayGroup = radio_PropsFrame_ScanDelayGroup_create(ip, ip->PropsControls);
	if (!ip->AutoScanGroup)
		ip->AutoScanGroup = radio_PropsFrame_AutoScanGroup_create(ip, ip->PropsControls);
	if (!ip->AutoReleaseItem)
		ip->AutoReleaseItem = radio_PropsFrame_AutoReleaseItem_create(ip, ip->PropsControls);
	if (!ip->PropsChoiceGroup)
		ip->PropsChoiceGroup = radio_PropsFrame_PropsChoiceGroup_create(ip, ip->PropsControls);
	if (!ip->OutputItem)
		ip->OutputItem = radio_PropsFrame_OutputItem_create(ip, ip->PropsControls);
	if (!ip->PropsAllGroup)
		ip->PropsAllGroup = radio_PropsFrame_PropsAllGroup_create(ip, ip->PropsControls);
	if (!ip->ApplyButton)
		ip->ApplyButton = radio_PropsFrame_ApplyButton_create(ip, ip->PropsControls);
	if (!ip->ResetButton)
		ip->ResetButton = radio_PropsFrame_ResetButton_create(ip, ip->PropsControls);
	if (!ip->ApplyResetGroup)
		ip->ApplyResetGroup = radio_PropsFrame_ApplyResetGroup_create(ip, ip->PropsControls);
	window_fit(ip->PropsControls);
	
	window_fit(ip->PropsFrame);
	
	xv_set(ip->PropsControls,
		XV_WIDTH, WIN_EXTEND_TO_EDGE,
		XV_HEIGHT, WIN_EXTEND_TO_EDGE,
		NULL);
	return ip;
}

