/*
 * Initialize an instance of object `repeat_win'.
 */
ae_repeat_win_objects *
ae_repeat_win_objects_initialize(ae_repeat_win_objects *ip, Xv_opaque owner)
{
	if (!ip && !(ip = (ae_repeat_win_objects *) calloc(1, sizeof (ae_repeat_win_objects))))
		return (ae_repeat_win_objects *) NULL;
	if (!ip->repeat_win)
		ip->repeat_win = ae_repeat_win_repeat_win_create(ip, owner);
	if (!ip->repeat_control)
		ip->repeat_control = ae_repeat_win_repeat_control_create(ip, ip->repeat_win);
	if (!ip->repeatunit)
		ip->repeatunit = ae_repeat_win_repeatunit_create(ip, ip->repeat_control);
	if (!ip->repeat_menu)
		ip->repeat_menu = ae_repeat_win_repeat_menu_create(ip, ip->repeat_control);
	if (!ip->repeatunitmessage)
		ip->repeatunitmessage = ae_repeat_win_repeatunitmessage_create(ip, ip->repeat_control);
	if (!ip->group11)
		ip->group11 = ae_repeat_win_group11_create(ip, ip->repeat_control);
	if (!ip->apply_button)
		ip->apply_button = ae_repeat_win_apply_button_create(ip, ip->repeat_control);
	if (!ip->group12)
		ip->group12 = ae_repeat_win_group12_create(ip, ip->repeat_control);
	window_fit(ip->repeat_control);
	
	window_fit(ip->repeat_win);
	
	xv_set(ip->repeat_control,
		XV_WIDTH, WIN_EXTEND_TO_EDGE,
		XV_HEIGHT, WIN_EXTEND_TO_EDGE,
		NULL);
	return ip;
}

