/*
 * Initialize an instance of object `mframe'.
 */
filemgr_mframe_objects *
filemgr_mframe_objects_initialize(filemgr_mframe_objects *ip, Xv_opaque owner)
{
	if (!ip && !(ip = (filemgr_mframe_objects *) calloc(1, sizeof (filemgr_mframe_objects))))
		return (filemgr_mframe_objects *) NULL;
	if (!ip->mframe)
		ip->mframe = filemgr_mframe_mframe_create(ip, owner);
	if (!ip->mcontrol_panel)
		ip->mcontrol_panel = filemgr_mframe_mcontrol_panel_create(ip, ip->mframe);
	if (!ip->mfile_button)
		ip->mfile_button = filemgr_mframe_mfile_button_create(ip, ip->mcontrol_panel);
	if (!ip->mview_button)
		ip->mview_button = filemgr_mframe_mview_button_create(ip, ip->mcontrol_panel);
	if (!ip->medit_button)
		ip->medit_button = filemgr_mframe_medit_button_create(ip, ip->mcontrol_panel);
	if (!ip->mgoto_button)
		ip->mgoto_button = filemgr_mframe_mgoto_button_create(ip, ip->mcontrol_panel);
	if (!ip->mgoto_line)
		ip->mgoto_line = filemgr_mframe_mgoto_line_create(ip, ip->mcontrol_panel);
	if (!ip->main_group)
		ip->main_group = filemgr_mframe_main_group_create(ip, ip->mcontrol_panel);
	window_fit(ip->mcontrol_panel);
	
	if (!ip->mpcanvas)
		ip->mpcanvas = filemgr_mframe_mpcanvas_create(ip, ip->mframe);
	if (!ip->mstatus_panel)
		ip->mstatus_panel = filemgr_mframe_mstatus_panel_create(ip, ip->mframe);
	if (!ip->eject_button)
		ip->eject_button = filemgr_mframe_eject_button_create(ip, ip->mstatus_panel);
	if (!ip->mstatus_glyph)
		ip->mstatus_glyph = filemgr_mframe_mstatus_glyph_create(ip, ip->mstatus_panel);
	if (!ip->mstatus_left)
		ip->mstatus_left = filemgr_mframe_mstatus_left_create(ip, ip->mstatus_panel);
	if (!ip->mstatus_right)
		ip->mstatus_right = filemgr_mframe_mstatus_right_create(ip, ip->mstatus_panel);
	if (!ip->mcanvas)
		ip->mcanvas = filemgr_mframe_mcanvas_create(ip, ip->mframe);
	window_fit(ip->mframe);
	
	xv_set(ip->mcontrol_panel, XV_WIDTH, WIN_EXTEND_TO_EDGE, NULL);
	xv_set(ip->mpcanvas, XV_WIDTH, WIN_EXTEND_TO_EDGE, NULL);
	xv_set(ip->mstatus_panel, XV_WIDTH, WIN_EXTEND_TO_EDGE, NULL);
	xv_set(ip->mcanvas,
		XV_WIDTH, WIN_EXTEND_TO_EDGE,
		XV_HEIGHT, WIN_EXTEND_TO_EDGE,
		NULL);
	return ip;
}

