/*
 * Initialize an instance of object `find_frame'.
 */
filemgr_find_frame_objects *
filemgr_find_frame_objects_initialize(filemgr_find_frame_objects *ip, Xv_opaque owner)
{
	if (!ip && !(ip = (filemgr_find_frame_objects *) calloc(1, sizeof (filemgr_find_frame_objects))))
		return (filemgr_find_frame_objects *) NULL;
	if (!ip->find_frame)
		ip->find_frame = filemgr_find_frame_find_frame_create(ip, owner);
	if (!ip->find_panel)
		ip->find_panel = setup_cmd_panel(ip, ip->find_frame);
	if (!ip->Typeitem)
		ip->Typeitem = filemgr_find_frame_Typeitem_create(ip, ip->find_panel);
	if (!ip->Fromitem)
		ip->Fromitem = filemgr_find_frame_Fromitem_create(ip, ip->find_panel);
	if (!ip->Nameitem)
		ip->Nameitem = filemgr_find_frame_Nameitem_create(ip, ip->find_panel);
	if (!ip->Owneritem)
		ip->Owneritem = filemgr_find_frame_Owneritem_create(ip, ip->find_panel);
	if (!ip->Afteritem)
		ip->Afteritem = filemgr_find_frame_Afteritem_create(ip, ip->find_panel);
	if (!ip->Beforeitem)
		ip->Beforeitem = filemgr_find_frame_Beforeitem_create(ip, ip->find_panel);
	if (!ip->Patternitem)
		ip->Patternitem = filemgr_find_frame_Patternitem_create(ip, ip->find_panel);
	if (!ip->find_group3)
		ip->find_group3 = filemgr_find_frame_find_group3_create(ip, ip->find_panel);
	if (!ip->Open_button)
		ip->Open_button = filemgr_find_frame_Open_button_create(ip, ip->find_panel);
	if (!ip->Find_button)
		ip->Find_button = filemgr_find_frame_Find_button_create(ip, ip->find_panel);
	if (!ip->Stop_button)
		ip->Stop_button = filemgr_find_frame_Stop_button_create(ip, ip->find_panel);
	if (!ip->find_group4)
		ip->find_group4 = filemgr_find_frame_find_group4_create(ip, ip->find_panel);
	if (!ip->find_group5)
		ip->find_group5 = filemgr_find_frame_find_group5_create(ip, ip->find_panel);
	if (!ip->Find_list)
		ip->Find_list = filemgr_find_frame_Find_list_create(ip, ip->find_panel);
	if (!ip->find_group6)
		ip->find_group6 = filemgr_find_frame_find_group6_create(ip, ip->find_panel);
	if (!ip->Nametoggle)
		ip->Nametoggle = filemgr_find_frame_Nametoggle_create(ip, ip->find_panel);
	if (!ip->Ownertoggle)
		ip->Ownertoggle = filemgr_find_frame_Ownertoggle_create(ip, ip->find_panel);
	if (!ip->after_date_pi)
		ip->after_date_pi = filemgr_find_frame_after_date_pi_create(ip, ip->find_panel);
	if (!ip->before_date_pi)
		ip->before_date_pi = filemgr_find_frame_before_date_pi_create(ip, ip->find_panel);
	if (!ip->Casetoggle)
		ip->Casetoggle = filemgr_find_frame_Casetoggle_create(ip, ip->find_panel);
	window_fit(ip->find_panel);
	
	window_fit(ip->find_frame);
	
	xv_set(ip->find_panel,
		XV_WIDTH, WIN_EXTEND_TO_EDGE,
		XV_HEIGHT, WIN_EXTEND_TO_EDGE,
		NULL);
	return ip;
}

