/*
 * Initialize an instance of object `rcp_frame'.
 */
filemgr_rcp_frame_objects *
filemgr_rcp_frame_objects_initialize(filemgr_rcp_frame_objects *ip, Xv_opaque owner)
{
	if (!ip && !(ip = (filemgr_rcp_frame_objects *) calloc(1, sizeof (filemgr_rcp_frame_objects))))
		return (filemgr_rcp_frame_objects *) NULL;
	if (!ip->rcp_frame)
		ip->rcp_frame = filemgr_rcp_frame_rcp_frame_create(ip, owner);
	if (!ip->remote_panel)
		ip->remote_panel = setup_cmd_panel(ip, ip->rcp_frame);
	if (!ip->rcp_sourcem)
		ip->rcp_sourcem = filemgr_rcp_frame_rcp_sourcem_create(ip, ip->remote_panel);
	if (!ip->rcp_smach)
		ip->rcp_smach = filemgr_rcp_frame_rcp_smach_create(ip, ip->remote_panel);
	if (!ip->Source_mc)
		ip->Source_mc = filemgr_rcp_frame_Source_mc_create(ip, ip->remote_panel);
	if (!ip->rcp_spath)
		ip->rcp_spath = filemgr_rcp_frame_rcp_spath_create(ip, ip->remote_panel);
	if (!ip->Source)
		ip->Source = filemgr_rcp_frame_Source_create(ip, ip->remote_panel);
	if (!ip->rcp_group2)
		ip->rcp_group2 = filemgr_rcp_frame_rcp_group2_create(ip, ip->remote_panel);
	if (!ip->rcp_destm)
		ip->rcp_destm = filemgr_rcp_frame_rcp_destm_create(ip, ip->remote_panel);
	if (!ip->rcp_dmach)
		ip->rcp_dmach = filemgr_rcp_frame_rcp_dmach_create(ip, ip->remote_panel);
	if (!ip->Destination_mc)
		ip->Destination_mc = filemgr_rcp_frame_Destination_mc_create(ip, ip->remote_panel);
	if (!ip->rcp_dpath)
		ip->rcp_dpath = filemgr_rcp_frame_rcp_dpath_create(ip, ip->remote_panel);
	if (!ip->Destination)
		ip->Destination = filemgr_rcp_frame_Destination_create(ip, ip->remote_panel);
	if (!ip->rcp_group1)
		ip->rcp_group1 = filemgr_rcp_frame_rcp_group1_create(ip, ip->remote_panel);
	if (!ip->Copy_button)
		ip->Copy_button = filemgr_rcp_frame_Copy_button_create(ip, ip->remote_panel);
	if (!ip->rcp_group3)
		ip->rcp_group3 = filemgr_rcp_frame_rcp_group3_create(ip, ip->remote_panel);
	if (!ip->rcp_cancel)
		ip->rcp_cancel = filemgr_rcp_frame_rcp_cancel_create(ip, ip->remote_panel);
	window_fit(ip->remote_panel);
	
	window_fit(ip->rcp_frame);
	
	xv_set(ip->remote_panel,
		XV_WIDTH, WIN_EXTEND_TO_EDGE,
		XV_HEIGHT, WIN_EXTEND_TO_EDGE,
		NULL);
	return ip;
}

