/*
 * Initialize an instance of object `comments_frame'.
 */
filemgr_comments_frame_objects *
filemgr_comments_frame_objects_initialize(filemgr_comments_frame_objects *ip, Xv_opaque owner)
{
	if (!ip && !(ip = (filemgr_comments_frame_objects *) calloc(1, sizeof (filemgr_comments_frame_objects))))
		return (filemgr_comments_frame_objects *) NULL;
	if (!ip->comments_frame)
		ip->comments_frame = filemgr_comments_frame_comments_frame_create(ip, owner);
	if (!ip->comments_top_panel) {
	    ip->comments_top_panel = setup_cmd_panel(ip, ip->comments_frame);
	    xv_set(ip->comments_top_panel, XV_HEIGHT, 95, NULL);
	}
	if (!ip->comments1)
		ip->comments1 = filemgr_comments_frame_comments1_create(ip, ip->comments_top_panel);
	if (!ip->comments2)
		ip->comments2 = filemgr_comments_frame_comments2_create(ip, ip->comments_top_panel);
	if (!ip->comments_group1)
		ip->comments_group1 = filemgr_comments_frame_comments_group1_create(ip, ip->comments_top_panel);
	if (!ip->comments3)
		ip->comments3 = filemgr_comments_frame_comments3_create(ip, ip->comments_top_panel);
	if (!ip->comments_group2)
		ip->comments_group2 = filemgr_comments_frame_comments_group2_create(ip, ip->comments_top_panel);
	window_fit(ip->comments_top_panel);
	
	if (!ip->comments_text)
		ip->comments_text = filemgr_comments_frame_comments_text_create(ip, ip->comments_frame);
	if (!ip->comments_bottom_panel)
		ip->comments_bottom_panel = filemgr_comments_frame_comments_bottom_panel_create(ip, ip->comments_frame);
	if (!ip->comments4)
		ip->comments4 = filemgr_comments_frame_comments4_create(ip, ip->comments_bottom_panel);
	if (!ip->comments_send_b)
		ip->comments_send_b = filemgr_comments_frame_comments_send_b_create(ip, ip->comments_bottom_panel);
	if (!ip->comments_cancel_b)
		ip->comments_cancel_b = filemgr_comments_frame_comments_cancel_b_create(ip, ip->comments_bottom_panel);
	if (!ip->comments_group3)
		ip->comments_group3 = filemgr_comments_frame_comments_group3_create(ip, ip->comments_bottom_panel);
	if (!ip->comments_group4)
		ip->comments_group4 = filemgr_comments_frame_comments_group4_create(ip, ip->comments_bottom_panel);
	window_fit(ip->comments_bottom_panel);
	
	window_fit(ip->comments_frame);
	
	xv_set(ip->comments_top_panel, XV_WIDTH, WIN_EXTEND_TO_EDGE, NULL);
	xv_set(ip->comments_text, XV_WIDTH, WIN_EXTEND_TO_EDGE, NULL);
	xv_set(ip->comments_bottom_panel,
		XV_WIDTH, WIN_EXTEND_TO_EDGE,
		XV_HEIGHT, WIN_EXTEND_TO_EDGE,
		NULL);
	return ip;
}

