/*
 * Initialize an instance of object `tframe'.
 */
filemgr_tframe_objects *
filemgr_tframe_objects_initialize(filemgr_tframe_objects *ip, Xv_opaque owner)
{
	if (!ip && !(ip = (filemgr_tframe_objects *) calloc(1, sizeof (filemgr_tframe_objects))))
		return (filemgr_tframe_objects *) NULL;
	if (!ip->tframe)
		ip->tframe = filemgr_tframe_tframe_create(ip, owner);
	if (!ip->tcontrol_panel)
		ip->tcontrol_panel = filemgr_tframe_tcontrol_panel_create(ip, ip->tframe);
	if (!ip->tfile_button)
		ip->tfile_button = filemgr_tframe_tfile_button_create(ip, ip->tcontrol_panel);
	if (!ip->tview_button)
		ip->tview_button = filemgr_tframe_tview_button_create(ip, ip->tcontrol_panel);
	if (!ip->tgoto_button)
		ip->tgoto_button = filemgr_tframe_tgoto_button_create(ip, ip->tcontrol_panel);
	if (!ip->tree_group)
		ip->tree_group = filemgr_tframe_tree_group_create(ip, ip->tcontrol_panel);
	window_fit(ip->tcontrol_panel);
	
	if (!ip->tcanvas)
		ip->tcanvas = filemgr_tframe_tcanvas_create(ip, ip->tframe);
	window_fit(ip->tframe);
	
	xv_set(ip->tcontrol_panel, XV_WIDTH, WIN_EXTEND_TO_EDGE, NULL);
	xv_set(ip->tcanvas,
		XV_WIDTH, WIN_EXTEND_TO_EDGE,
		XV_HEIGHT, WIN_EXTEND_TO_EDGE,
		NULL);
	return ip;
}

